'use strict';

module.exports = function() {
  $.gulp.task('image:min', function() {
    return $.gulp.src('./source/images/**/*.*')
      .pipe($.gp.imagemin({
        interlaced: true, progressive: true,
        svgoPlugins: [{ removeViewBox: false }],
        use: [$.gp.pngquant()]}))
      .pipe($.gulp.dest($.config.root + '/assets/img'));
  })
};
